package finTech;

public class additon {
	
	public static int add(int a,int b) {
		return a+b;
	}
	
	public static float add(float a , float b) {
		return a+b;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.print(additon.add(10.2f,6.9f));
	}

}
